import React, { useState, useMemo } from 'react';
import styled from 'styled-components';
import { Heart, Star, Users, ChevronDown } from 'lucide-react';

// ==================== STYLED COMPONENTS ====================

const PageSection = styled.section`
	padding: 2rem 0;
`;

const PageTitle = styled.h1`
	font-size: 2.25rem;
	font-weight: 700;
	text-align: center;
	margin-bottom: 2rem;
	color: ${props => (props.darkMode ? '#ffffff' : '#111827')};
`;

const FilterSection = styled.div`
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 1rem;
	margin-bottom: 2rem;
`;

const FilterInput = styled.input`
	width: 100%;
	padding: 0.75rem 1rem;
	border-radius: 0.5rem;
	border: 1px solid ${props => (props.darkMode ? '#4b5563' : '#d1d5db')};
	background-color: ${props => (props.darkMode ? '#374151' : '#ffffff')};
	color: ${props => (props.darkMode ? '#ffffff' : '#000000')};
	font-size: 1rem;

	&::placeholder {
		color: ${props => (props.darkMode ? '#9ca3af' : '#9ca3af')};
	}

	&:focus {
		outline: none;
		border-color: #a855f7;
		box-shadow: 0 0 0 3px rgba(168, 85, 247, 0.1);
	}
`;

const SelectInput = styled.select`
	width: 100%;
	padding: 0.75rem 1rem;
	border-radius: 0.5rem;
	border: 1px solid ${props => (props.darkMode ? '#4b5563' : '#d1d5db')};
	background-color: ${props => (props.darkMode ? '#374151' : '#ffffff')};
	color: ${props => (props.darkMode ? '#ffffff' : '#000000')};
	font-size: 1rem;

	&:focus {
		outline: none;
		border-color: #a855f7;
		box-shadow: 0 0 0 3px rgba(168, 85, 247, 0.1);
	}
`;

const MiracleCard = styled.div`
	background-color: ${props => (props.darkMode ? '#1f2937' : '#ffffff')};
	border-radius: 0.75rem;
	box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
	overflow: hidden;
	transition: all 0.3s ease;
	margin-bottom: 1.5rem;

	&:hover {
		transform: translateY(-4px);
		box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
	}
`;

const MiracleCardHeader = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: center;
	padding: 1.5rem;
	background: linear-gradient(135deg, rgba(168, 85, 247, 0.1), rgba(236, 72, 153, 0.1));
	cursor: pointer;
	gap: 5rem;
`;

const MiracleIcon = styled.span`
	font-size: 2.5rem;
	display: flex;
	align-items: center;
	flex-shrink: 0;
	margin-left: 0;

	img {
		width: 2.5rem;
		height: 2.5rem;
		object-fit: contain;
	}
`;

const MiracleInfo = styled.div`
	flex: 1;
	margin-left: 8rem;
`;

const MiracleYear = styled.span`
	display: inline-block;
	background-color: #a855f7;
	color: white;
	padding: 0.25rem 0.75rem;
	border-radius: 9999px;
	font-size: 0.875rem;
	margin-bottom: 0.5rem;
`;

const MiracleTitle = styled.h3`
	font-size: 1.25rem;
	font-weight: 700;
	margin-bottom: 0.25rem;
	color: ${props => (props.darkMode ? '#ffffff' : '#111827')};
`;

const MiracleCategory = styled.span`
	display: inline-block;
	background-color: ${props => (props.darkMode ? '#374151' : '#e5e7eb')};
	color: ${props => (props.darkMode ? '#d1d5db' : '#374151')};
	padding: 0.25rem 0.75rem;
	border-radius: 0.375rem;
	font-size: 0.875rem;
	margin-right: 0.5rem;
`;

const MiracleStats = styled.div`
	display: flex;
	gap: 1.5rem;
	align-items: center;
	justify-content: flex-end;
`;

const Stat = styled.div`
	display: flex;
	align-items: center;
	gap: 0.25rem;
	color: ${props => (props.darkMode ? '#9ca3af' : '#6b7280')};
	font-size: 0.875rem;
`;

const IconButton = styled.button`
	background: none;
	border: none;
	cursor: pointer;
	padding: 0.5rem;
	display: flex;
	align-items: center;
	justify-content: center;
	color: ${props => (props.darkMode ? '#e5e7eb' : '#374151')};
	transition: color 0.3s ease;

	&:hover {
		color: #ec4899;
	}
`;

const MiracleContent = styled.div`
	padding: 1.5rem;
	max-height: ${props => (props.expanded ? '100%' : '0')};
	overflow: ${props => (props.expanded ? 'visible' : 'hidden')};
	transition: max-height 0.3s ease;
`;

const MiracleStory = styled.div`
	color: ${props => (props.darkMode ? '#d1d5db' : '#374151')};
	line-height: 1.6;

	p {
		margin-bottom: 1rem;

		&:last-child {
			margin-bottom: 0;
		}
	}
`;

const EmptyState = styled.div`
	text-align: center;
	padding: 3rem 1rem;
	color: ${props => (props.darkMode ? '#9ca3af' : '#6b7280')};
`;

const EmptyIcon = styled.div`
	font-size: 3rem;
	margin-bottom: 1rem;
`;

const StatsGrid = styled.div`
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 1rem;
	margin-bottom: 2rem;
`;

const StatCard = styled.div`
	background-color: ${props => (props.darkMode ? '#1f2937' : '#ffffff')};
	border-radius: 0.75rem;
	box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
	padding: 1.5rem;
	text-align: center;
`;

const StatValue = styled.div`
	font-size: 2rem;
	font-weight: 700;
	margin-bottom: 0.5rem;
	color: ${props => props.color || '#a855f7'};
`;

const StatLabel = styled.div`
	font-size: 0.875rem;
	color: ${props => (props.darkMode ? '#9ca3af' : '#6b7280')};
`;

const ChevronIcon = styled(ChevronDown)`
	transition: transform 0.3s ease;
	transform: ${props => (props.expanded ? 'rotate(180deg)' : 'rotate(0deg)')};
`;
const PsalmSection = styled.section`
	background: linear-gradient(135deg, #a855f7 0%, #ec4899 100%);
	color: white;
	padding: 3rem 1rem;
	text-align: center;
	border-radius: 1rem;
	margin-top: 3rem;
	box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
`;

const PsalmTitle = styled.h2`
	font-size: 1.5rem;
	font-weight: 700;
	margin-bottom: 1rem;
	opacity: 0.95;
`;

const PsalmText = styled.p`
	font-size: 1.125rem;
	line-height: 1.8;
	opacity: 0.9;
	max-width: 600px;
	margin: 0 auto;
`;
// ==================== CUSTOM HOOKS ====================

const useLocalStorage = (key, initialValue) => {
	const [storedValue, setStoredValue] = React.useState(() => {
		try {
			const item = typeof window !== 'undefined' ? window.localStorage.getItem(key) : null;
			return item ? JSON.parse(item) : initialValue;
		} catch {
			return initialValue;
		}
	});

	const setValue = value => {
		try {
			setStoredValue(value);
			if (typeof window !== 'undefined') {
				window.localStorage.setItem(key, JSON.stringify(value));
			}
		} catch (error) {
			console.error(error);
		}
	};

	return [storedValue, setValue];
};

const useDebounce = (value, delay) => {
	const [debouncedValue, setDebouncedValue] = React.useState(value);

	React.useEffect(() => {
		const handler = setTimeout(() => setDebouncedValue(value), delay);
		return () => clearTimeout(handler);
	}, [value, delay]);

	return debouncedValue;
};

// ==================== MAIN COMPONENT ====================

const MyMiraclesPage = ({ darkMode }) => {
	const [expandedMiracle, setExpandedMiracle] = useState(null);
	const [searchQuery, setSearchQuery] = useState('');
	const [selectedCategory, setSelectedCategory] = useState('all');
	const [sortBy, setSortBy] = useState('date');
	const [favorites, setFavorites] = useLocalStorage('my-miracles-favorites', []);

	const debouncedSearch = useDebounce(searchQuery, 300);

	// Your miracles data
	const myMiracles = useMemo(
		() => [
			{
				id: 1,
				year: '2026',
				title: '×”× ×¡ ×”×¢×ª×™×“×™ ×©×× ×™ ×•×ž×¨×’×œ×™×ª ×ž×—×›×™× ×œ×• ×›×œ ×™×•×',
				story: (
					<>
						<p style={{ marginBottom: '0.5rem' }}>×‘×¢×–×¨×ª ×”×©× ×× ×™ ×•×ž×¨×’×œ×™×ª × ×–×›×” ×œ×–×¨×¢ ×‘×¨ ×§×™×™×ž× ×‘× ×™× ×•×‘× ×•×ª ×¦×“×™×§×™× ×•×¦×“×™×§×•×ª.</p>
					</>
				),
				category: '×ž×©×¤×—×”',
				icon: 'ðŸ‘¨â€ðŸ‘©â€ðŸ‘¦',
				impact: 6,
				views: 145,
				favorite: false,
			},
			{
				id: 1,
				year: '2025',
				title: '×”× ×¡ ×”×¢×ª×™×“×™ ×©×›×•×œ× ×• ×ž×—×›×™× ×œ×• ×›×œ ×™×•×',
				story: (
					<>
						<p style={{ marginBottom: '0.5rem' }}>×©×™×‘× ×” ×‘×™×ª ×”×ž×§×“×© ×‘×ž×”×¨×” ×‘×™×ž×™×™× ×• ××ž×ŸðŸ™,</p>
					</>
				),
				category: '×’××•×œ×”',
				icon: './Solvation.ico',
				impact: 10,
				views: 145,
				favorite: false,
			},
			{
				id: 2,
				year: '2021',
				title: '×”×©×™×“×•×š ×”×©× ×™ ×•×”×ž×•×¦×œ×—',
				story: (
					<>
