		return Object.entries(categories).map(([name, value]) => ({ name, value }));
	}, [myMiracles]);

	const impactData = useMemo(() => {
		return myMiracles.map(m => ({ name: m.title.slice(0, 10), impact: m.impact, views: m.views }));
